INSERT INTO public.curriculum (curriculum_type, language, subject, grade, semester, chapter, topic)
VALUES
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 1', 'Chapter 0: The Beginning', 'Kompetensi: Mengenal pentingnya belajar Bahasa Inggris, Memahami cara belajar Bahasa Inggris yang efektif, Menumbuhkan rasa percaya diri dalam menggunakan Bahasa Inggris di kelas'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 1', 'Chapter 0: The Beginning', 'Grammar: Pengenalan pronouns (I, you, we, they), Penggunaan "to be": is, am, are'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 1', 'Chapter 0: The Beginning', 'Language Features: Kalimat kelas dasar: “May I go to the toilet?”, “I don’t understand”, “How do you say ... in English?”, Ekspresi partisipasi di kelas: listen, repeat, raise your hand'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 1', 'Chapter 0: The Beginning', 'Vocabulary: Kosakata kelas: book, pen, teacher, whiteboard, Kata kerja dasar: read, write, speak, listen'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 1', 'Chapter 1: Greetings and Partings', 'Grammar: Kalimat sederhana dengan “to be”: “I am …”, “You are …”, Kalimat tanya: “How are you?”, “What is your name?”'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 1', 'Chapter 1: Greetings and Partings', 'Language Features: Greeting expressions: “Hello”, “Hi”, “Good morning”, “Nice to meet you”, Parting expressions: “Goodbye”, “See you later”, “Have a nice day”'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 1', 'Chapter 1: Greetings and Partings', 'Vocabulary: Waktu dalam salam: morning, afternoon, evening, night, Kata-kata sapaan: friend, teacher, classmate'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 1', 'Chapter 2: Thanking and Apologizing', 'Grammar: Kalimat dengan subject + verb: “I thank you”, “I’m sorry”, Penggunaan modal expression: “May I…”, “Can I…”'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 1', 'Chapter 2: Thanking and Apologizing', 'Language Features: Thanking expressions: “Thank you very much”, “Thanks”, “I appreciate it”, Apologizing expressions: “I’m sorry”, “Excuse me”, “I didn’t mean to”'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 1', 'Chapter 2: Thanking and Apologizing', 'Vocabulary: Kata kerja sopan: apologize, forgive, help, Situasi sosial: late, borrow, disturb'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 1', 'Chapter 3: Introducing Oneself and Others', 'Grammar: Kalimat perkenalan: “My name is…”, “I live in…”, Pronouns: I, you, he, she, they, To be: am, is, are'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 1', 'Chapter 3: Introducing Oneself and Others', 'Language Features: Asking names: “What’s your name?”, Introducing others: “This is my friend…”, “May I introduce…?”'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 1', 'Chapter 3: Introducing Oneself and Others', 'Vocabulary: Nama tempat: school, city, country, Kata sifat dasar: friendly, smart, nice'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 1', 'Chapter 4: My Family and Me', 'Grammar: Possessive adjectives: my, your, his, her, Simple present tense (positive & negative): “She has a brother.” / “I don’t have a sister.”'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 1', 'Chapter 4: My Family and Me', 'Language Features: Describing people: “He is tall.”, “She has long hair.”, Asking about family: “How many brothers do you have?”'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 1', 'Chapter 4: My Family and Me', 'Vocabulary: Anggota keluarga: father, mother, cousin, uncle, aunt, Ciri fisik: tall, short, slim, chubby, black hair'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 1', 'Chapter 5: Numbers, Dates, and Time', 'Grammar: Question forms: “What time is it?”, “When is your birthday?”, Prepositions of time: in, on, at'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 1', 'Chapter 5: Numbers, Dates, and Time', 'Language Features: Telling time: “It’s ten o’clock”, “It’s half past seven”, Talking about dates: “My birthday is on March 3rd.”'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 1', 'Chapter 5: Numbers, Dates, and Time', 'Vocabulary: Angka: one to one hundred, Hari dan bulan: Monday to Sunday, January to December, Waktu: hour, minute, morning, night'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 2', 'Chapter 4: My School Activities', 'Kompetensi: Menyebutkan dan menjelaskan kegiatan harian di sekolah, Menyusun kalimat sederhana tentang rutinitas dan waktu'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 2', 'Chapter 4: My School Activities', 'Grammar: Simple Present Tense: untuk menyatakan kebiasaan ("I study at 8 a.m."), Adverbs of Frequency: always, usually, sometimes, never, Prepositions of Time: in, on, at (contoh: "at 7 o''clock", "on Monday")'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 2', 'Chapter 4: My School Activities', 'Language Features: Pola kalimat positif dan negatif kegiatan sehari-hari, Penggunaan waktu dalam konteks kegiatan (before class, after break)'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 2', 'Chapter 4: My School Activities', 'Vocabulary: Kegiatan sekolah: study, read, write, discuss, sing, play, listen, Jadwal & waktu: Monday, Tuesday, 8 a.m., 12 o’clock, lunch break, flag ceremony, Tempat kegiatan: classroom, library, canteen, lab'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 2', 'Chapter 5: This is My School', 'Kompetensi: Menunjukkan lokasi dan bagian-bagian sekolah, Mendeskripsikan tempat dan benda dengan kata sifat'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 2', 'Chapter 5: This is My School', 'Grammar: Prepositions of Place: in, on, under, next to, behind, in front of, Articles: a, an, the ("This is a classroom." / "The library is big."), Demonstratives: this, that, these, those'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 2', 'Chapter 5: This is My School', 'Language Features: Kalimat deskriptif tempat dan lokasi ("The canteen is next to the office."), Kalimat tanya tentang lokasi dan deskripsi ("Where is the library?", "What is it like?")'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 2', 'Chapter 5: This is My School', 'Vocabulary: Tempat di sekolah: classroom, school yard, teachers’ room, library, laboratory, Deskripsi tempat: clean, big, small, crowded, quiet, Alat bantu sekolah: board, eraser, marker, globe, microscope'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 2', 'Chapter 6: Describing People, Objects, and Animals', 'Kompetensi: Mendeskripsikan penampilan fisik orang, Mendeskripsikan ciri-ciri benda dan hewan secara sederhana'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 2', 'Chapter 6: Describing People, Objects, and Animals', 'Grammar: Adjectives: big, small, tall, short, kind, beautiful, To be + Adjective: "She is tall." / "It is fluffy.", Singular and Plural Nouns: "a dog", "three dogs"'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 2', 'Chapter 6: Describing People, Objects, and Animals', 'Language Features: Deskripsi sederhana menggunakan adjective + noun, Kalimat tanya deskriptif: "What does he look like?" / "What is it?" / "How many legs does it have?"'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 7 (SMP)', 'Semester 2', 'Chapter 6: Describing People, Objects, and Animals', 'Vocabulary: Deskripsi orang: short hair, long hair, dark skin, curly hair, Benda: book, pencil, chair, bottle, bag, Hewan: cat, dog, rabbit, elephant, bird, Ciri khas hewan: furry, big ears, long trunk, sharp teeth');
INSERT INTO public.curriculum (curriculum_type, language, subject, grade, semester, chapter, topic)
VALUES
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 1', 'Chapter 0: The Beginning', 'Kompetensi: Mengenal tujuan belajar Bahasa Inggris, Mengenali cara belajar yang efektif dan kebiasaan baik dalam belajar, Mengenalkan aktivitas berbicara, membaca, menulis, dan mendengarkan dalam kelas'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 1', 'Chapter 0: The Beginning', 'Grammar: Pengenalan ulang: pronouns (I, you, he, she, etc.), Kata kerja "to be": is, am, are dalam konteks sederhana, Kalimat tanya dasar: What''s your name? How old are you?'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 1', 'Chapter 0: The Beginning', 'Language Features: Salam formal dan informal, Bahasa sopan dalam kelas: “May I…”, “Can you help me…?”'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 1', 'Chapter 0: The Beginning', 'Vocabulary: Kosakata kelas dan pembelajaran: book, notebook, eraser, teacher, classmate, Kata kerja belajar: listen, write, read, say, understand'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 1', 'Chapter 1: Celebrating Independence Day', 'Kompetensi: Menyampaikan pengalaman merayakan Hari Kemerdekaan, Menggunakan past tense untuk menceritakan kejadian, Memberi ucapan selamat atau harapan kepada orang lain'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 1', 'Chapter 1: Celebrating Independence Day', 'Grammar: Past Simple Tense (regular and irregular verbs): "I joined a flag ceremony.", "We sang the national anthem.", Penggunaan kata waktu masa lalu: yesterday, last week, two days ago'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 1', 'Chapter 1: Celebrating Independence Day', 'Language Features: Ucapan selamat dan harapan: “Happy Independence Day!”, “I hope we stay united.”, Struktur narasi sederhana: membuka – isi – penutup'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 1', 'Chapter 1: Celebrating Independence Day', 'Vocabulary: Kata kerja kegiatan: join, sing, march, celebrate, raise, Kata benda: flag, uniform, ceremony, speech, independence, Ekspresi: proud, happy, together, brave'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 1', 'Chapter 2: Kindness Begins with Me', 'Kompetensi: Menjelaskan bentuk kebaikan dalam kehidupan, Menyusun teks naratif sederhana tentang pengalaman baik, Menunjukkan sikap positif melalui komunikasi lisan dan tulisan'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 1', 'Chapter 2: Kindness Begins with Me', 'Grammar: Past Tense untuk perbuatan kebaikan: helped, gave, shared, smiled, Kata sambung: and, because, but, so, Kalimat positif dan negatif: "I helped my friend.", "I didn’t forget to say thank you."'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 1', 'Chapter 2: Kindness Begins with Me', 'Language Features: Kalimat deskriptif tentang perilaku baik, Ekspresi niat baik: “I wanted to help.” / “I decided to say sorry.”, Struktur cerita pendek tentang kebaikan: orientation – event – message'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 1', 'Chapter 2: Kindness Begins with Me', 'Vocabulary: Kosakata kebaikan: help, care, give, share, respect, Kata sifat positif: kind, friendly, polite, honest, Ungkapan sosial: “Thank you for your help”, “You’re welcome”, “I’m proud of you”'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 2', 'Chapter 3: Love Our World', 'Subtema: Look Around You – Mengamati lingkungan sekitar, This is the Way – Langkah-langkah menjaga alam, Act Now – Ajakan dan tindakan nyata menyelamatkan bumi'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 2', 'Chapter 3: Love Our World', 'Grammar: Simple Present Tense (untuk fakta umum dan kebiasaan), Imperatives (untuk memberikan instruksi atau ajakan), Modals (should/must) untuk menyarankan tindakan'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 2', 'Chapter 3: Love Our World', 'Language Features: Expression of advice: You should recycle., Expression of encouragement: Let’s save energy!, Sentence connectors: first, then, finally'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 2', 'Chapter 3: Love Our World', 'Vocabulary: Environmental terms: reduce, reuse, recycle, pollution, garbage, Nature-related nouns: forest, ocean, animal, ecosystem'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 2', 'Chapter 4: No Littering', 'Grammar: Prohibition expressions: Don''t throw trash!, Present Continuous (describing what people are doing), Question Tags: You care about the environment, don’t you?'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 2', 'Chapter 4: No Littering', 'Language Features: Giving warnings and commands, Asking for clarification: What do you mean by “no littering”?'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 2', 'Chapter 4: No Littering', 'Vocabulary: Cleanliness terms: litter, trash, waste bin, sweep, Public space nouns: park, riverbank, classroom, street'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 2', 'Chapter 5: Embrace Yourself', 'Grammar: Adjectives (describing physical and character traits), Comparative and Superlative Adjectives, Expression of Wish: I wish I were more confident.'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 2', 'Chapter 5: Embrace Yourself', 'Language Features: Giving compliments: You look great today!, Expressing sympathy: I’m sorry to hear that., Expressing disappointment: That’s too bad.'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 2', 'Chapter 5: Embrace Yourself', 'Vocabulary: Adjectives: confident, kind, honest, shy, friendly, Self-concept: talent, dream, strength, weakness'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 2', 'Chapter 6: Text Types', 'Recount Text: Grammar: Simple Past Tense, Features: chronological order, time expressions, Vocabulary: went, saw, visited, yesterday, last weekend'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 2', 'Chapter 6: Text Types', 'Narrative Text: Grammar: Past Tense (verb & nominal), Features: characters, conflict, resolution, Vocabulary: suddenly, once upon a time, hero, villain'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 2', 'Chapter 6: Text Types', 'Descriptive Text: Grammar: Simple Present Tense, adjectives, Features: using senses to describe, Vocabulary: size, shape, color, texture'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 2', 'Chapter 6: Text Types', 'Procedure Text: Grammar: Imperative sentences, Sequence markers, Features: steps/instructions, Vocabulary: first, next, pour, mix, boil, stir'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 2', 'Chapter 7: Communication in Daily Life', 'Grammar: Phrasal Verbs: pick up, turn off, give up, Agreement and Disagreement: I agree with you. / I don’t think so.'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 2', 'Chapter 7: Communication in Daily Life', 'Language Features: Attracting attention: Excuse me, May I…?, Exclamations: What a beautiful day!, How nice!, Classroom language: raise your hand, may I ask?, be quiet'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 8 (SMP)', 'Semester 2', 'Chapter 7: Communication in Daily Life', 'Vocabulary: Measurement: centimeter, liter, kilogram, Transition signals: however, therefore, in addition, because');
INSERT INTO public.curriculum (curriculum_type, language, subject, grade, semester, chapter, topic)
VALUES
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 1: Exploring Fauna of Indonesia', 'Materi Pokok: Mengenal satwa khas Indonesia seperti orangutan, bekantan, cendrawasih, komodo, Mendeskripsikan hewan berdasarkan habitat, perilaku, dan ciri fisiknya, Menyusun informasi tentang fauna dalam kalimat deskriptif'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 1: Exploring Fauna of Indonesia', 'Grammar: Noun Groups (contoh: a big brown monkey), Past Tense (positive, negative, interrogative), Passive Voice (contoh: The orangutan is found in Borneo.)'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 1: Exploring Fauna of Indonesia', 'Language Features: Adjective order: size–color–origin–material–purpose, Sentence patterns: S + was/were + V3, Short responses: Yes, it was. / No, it didn’t.'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 1: Exploring Fauna of Indonesia', 'Vocabulary: Animal names and habitats: swamp, rainforest, endangered, Verbs: live, climb, protect, survive'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 1: Exploring Fauna of Indonesia', 'Text Type: Descriptive Text: Menjelaskan ciri, tempat hidup, dan fakta unik tentang fauna'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 2: Taking Trips', 'Materi Pokok: Menggambarkan pengalaman berlibur atau berkunjung ke suatu tempat, Menyusun cerita menggunakan urutan kejadian, Menanggapi pertanyaan tentang pengalaman pribadi'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 2: Taking Trips', 'Grammar: Simple Past Tense, Temporal Conjunctions (first, then, after that, finally), Wh- Questions in Past Tense (Where did you go? What did you see?)'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 2: Taking Trips', 'Language Features: Expression of sequence: After we arrived, we took pictures., Prepositions of place and movement: into, onto, through, along'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 2: Taking Trips', 'Vocabulary: Travel verbs: travel, visit, explore, stay, return, Places: beach, mountain, museum, historical site'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 2: Taking Trips', 'Text Type: Recount Text: Menceritakan perjalanan atau pengalaman pribadi'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 3: Journey to Fantasy Worlds', 'Materi Pokok: Menceritakan kembali cerita fantasi atau dongeng, Mengidentifikasi unsur narasi: tokoh, latar, konflik, penyelesaian, Menggunakan kalimat tidak langsung untuk menyampaikan ucapan atau pertanyaan'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 3: Journey to Fantasy Worlds', 'Grammar: Indirect Speech for Questions (contoh: She asked where the dragon lived.), Past Continuous Tense, Adverbs of Time & Place in Narrative'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 3: Journey to Fantasy Worlds', 'Language Features: Story openers: Once upon a time, Long ago..., Dialogue and narration: The hero said, “I will save the village.”'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 3: Journey to Fantasy Worlds', 'Vocabulary: Fantasy terms: magical, wizard, enchanted forest, battle, curse, Character traits: brave, clever, evil, mysterious'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 3: Journey to Fantasy Worlds', 'Text Type: Narrative Text: Cerita imajinatif seperti legenda, mitos, atau dongeng'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 4: Communicating Thoughts and Feelings', 'Materi Pokok: Mengungkapkan harapan, keinginan, dan selamat, Menyatakan persetujuan, ketidaksetujuan, dan niat, Memberikan respon dalam berbagai situasi sosial'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 4: Communicating Thoughts and Feelings', 'Grammar: Expression of Wish: I wish I had a pet dragon., Modals of Intention: I will, I’m going to, Would rather / Would like to'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 4: Communicating Thoughts and Feelings', 'Language Features: Expressions of sympathy: I’m sorry to hear that., Disappointment: That’s too bad., Attracting attention: Excuse me, May I...?'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 4: Communicating Thoughts and Feelings', 'Vocabulary: Expressions: wish, hope, feel sorry, congratulations, Transition signals: however, therefore, in contrast'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 4: Communicating Thoughts and Feelings', 'Text Type: Short dialogues, interpersonal exchanges, personal notes'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 5: How to Do It?', 'Materi Pokok: Menjelaskan proses atau langkah-langkah membuat sesuatu, Menyusun teks prosedur secara sistematis, Menggunakan imperatif dengan tepat'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 5: How to Do It?', 'Grammar: Imperative Sentences: Mix the ingredients. / Don’t forget to stir., Sequence Markers: First, Next, After that, Finally, Present Simple for Instruction'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 5: How to Do It?', 'Language Features: Action verbs: pour, chop, heat, boil, Tips and cautions: Be careful not to spill the water.'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 5: How to Do It?', 'Vocabulary: Daily objects and tools: scissors, glue, ruler, Kitchen tools: bowl, spoon, oven, knife'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 1', 'Chapter 5: How to Do It?', 'Text Type: Procedure Text: Langkah-langkah membuat kerajinan, resep, atau kegiatan'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 4: Digital Life', 'Materi Pokok: Mengenal kosakata dan frasa yang digunakan dalam konteks dunia digital, Mendeskripsikan kegiatan sehari-hari yang berhubungan dengan teknologi, Membandingkan penggunaan teknologi di masa lalu dan sekarang'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 4: Digital Life', 'Grammar: Present Continuous Tense (untuk tindakan sedang berlangsung), Present Perfect Tense (untuk pengalaman atau kejadian yang masih relevan), Adverbs of frequency: always, usually, sometimes, rarely'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 4: Digital Life', 'Vocabulary: Kata terkait teknologi: gadget, device, app, download, connect, Aktivitas digital: browsing, chatting, gaming, updating'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 4: Digital Life', 'Expressions: Expressing current actions: I’m using my tablet to..., Talking about experiences: I have downloaded this app before., Making comparisons: Nowadays, people rarely send letters.'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 4: Digital Life', 'Text Type: Descriptive Text, Informal Dialogue'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 5: Telling Stories (Narrative Text)', 'Materi Pokok: Menganalisis struktur teks naratif (orientation, complication, resolution), Membaca dan memahami cerita rakyat Indonesia dan dunia, Menyusun teks naratif sederhana'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 5: Telling Stories (Narrative Text)', 'Grammar: Simple Past Tense, Past Continuous Tense, Time Connectors: once upon a time, suddenly, eventually, finally'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 5: Telling Stories (Narrative Text)', 'Vocabulary: Cerita rakyat: prince, witch, dragon, spell, Kata kerja naratif: rescued, escaped, cursed, defeated'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 5: Telling Stories (Narrative Text)', 'Expressions: Expressing emotions: What a scary story!, That’s amazing!, Narration verbs: said, told, shouted, asked'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 5: Telling Stories (Narrative Text)', 'Text Type: Narrative Text (legend, fairy tale, fable)'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 6: Let’s Report It! (Report Text)', 'Materi Pokok: Membedakan antara teks laporan dan teks deskriptif, Menyusun teks laporan tentang fauna, tempat, atau fenomena umum, Menjelaskan struktur dan fitur kebahasaan teks laporan'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 6: Let’s Report It! (Report Text)', 'Grammar: Simple Present Tense, Passive Voice in Present Tense, Relative Clauses: which, that, who'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 6: Let’s Report It! (Report Text)', 'Vocabulary: Fauna Indonesia: hornbill, Sumatran tiger, anoa, Kata ilmiah atau teknis: habitat, behavior, classification, characteristic'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 6: Let’s Report It! (Report Text)', 'Expressions: Explaining facts: It is usually found in..., Reporting: According to scientists...'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 6: Let’s Report It! (Report Text)', 'Text Type: Report Text'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 7: It’s Your Turn! (Procedure Text)', 'Materi Pokok: Menyusun teks prosedur dengan urutan langkah yang sistematis, Membaca dan memahami label dan petunjuk produk, Mengenali fitur kebahasaan teks prosedur'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 7: It’s Your Turn! (Procedure Text)', 'Grammar: Imperative Sentences: Cut, Mix, Boil, Do not forget to..., Sequence Markers: first, next, then, after that, finally, Modals for Instruction: should, must, have to'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 7: It’s Your Turn! (Procedure Text)', 'Vocabulary: Proses: stir, pour, prepare, measure, Label terms: ingredients, direction, expiry date, storage'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 7: It’s Your Turn! (Procedure Text)', 'Expressions: Giving instructions: Make sure to..., Cautions: Be careful with hot water., Labels: Shake well before use.'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 7: It’s Your Turn! (Procedure Text)', 'Text Type: Procedure Text, Product Labels'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 8: Say It Right! (Expressing Hopes, Wishes, Intentions)', 'Materi Pokok: Mengungkapkan harapan dan keinginan secara sopan, Menyampaikan niat untuk melakukan sesuatu, Menyampaikan ucapan selamat dan menunjukkan simpati'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 8: Say It Right! (Expressing Hopes, Wishes, Intentions)', 'Grammar: Expression of Wish and Hope: I wish I could..., I hope you win., Expression of Intention: I’m going to..., I plan to..., Passive Voice in Present & Perfect Tense'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 8: Say It Right! (Expressing Hopes, Wishes, Intentions)', 'Vocabulary: Ungkapan harapan: success, future, dream, Ekspresi: congratulations, I’m sorry to hear that, I hope so'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 8: Say It Right! (Expressing Hopes, Wishes, Intentions)', 'Expressions: Wishes: I wish you all the best., Intentions: I’m planning to join the competition., Congratulation: Congratulations on your achievement!'),
('Kurikulum Merdeka Belajar', 'en', 'Bahasa Inggris', 'Grade 9 (SMP)', 'Semester 2', 'Chapter 8: Say It Right! (Expressing Hopes, Wishes, Intentions)', 'Text Type: Short Dialogues, Expressive Sentences');
